$('.btn_scroll').on('click',function(){let scrNum=$(this).attr('id')[7];let nextSec=$('.section'+(Number(scrNum)+1));let nextSecST=nextSec.offset();$('html, body').animate({scrollTop:nextSecST.top})})
$('.pagination').on('click',function(){let secT=$(this).parent().parent().parent().attr('class')[15];let thisP=$(this).attr('class')[16];if(secT==1){let thisSlide=$('.section1 .slide0'+thisP);$('.section1 .pagination').removeClass('active');thisActive(this);$('.section1 .slide').removeClass('fade_in');addFade(thisSlide)}else if(secT==2){let thisSlide=$('.section2 .game0'+thisP);$('.section2 .pagination').removeClass('active');thisActive(this);$('.section2 .game').removeClass('fade_in');addFade(thisSlide)}else if(secT==3){let thisSlide=$('.section3 .passion0'+thisP);$('.section3 .pagination').removeClass('active');thisActive(this);$('.section3 .passion').removeClass('fade_in');addFade(thisSlide)}})
let drag_x1=!1;$('.slider_1st .slider_container').on({mousedown:function(event){drag_x1=event.offsetX},mouseup:function(event){let pagination=$('.slider_1st .pagination');let slide=$('.slider_1st .slide');dragSlide1st(pagination,slide)}})
$('.updated .games_wrap').on({mousedown:function(event){drag_x1=event.offsetX},mouseup:function(event){let pagination=$('.updated .pagination');let slide=$('.updated .game');dragSlide(pagination,slide)}})
$('.sec_passion .passions_wrap').on({mousedown:function(event){drag_x1=event.offsetX},mouseup:function(event){let pagination=$('.sec_passion .pagination');let slide=$('.sec_passion .passion');dragSlide(pagination,slide)}})
function thisActive(self){$(self).addClass('active')}
function addFade(showSlide){showSlide.addClass('fade_in')}
function dragSlide1st(page,slide12345){let pagination=page;let slide=slide12345;if(event.offsetX-drag_x1>100){if(slide.eq(0).attr('class')[14]=='f'){slide.removeClass('fade_in');slide.eq(4).addClass('fade_in');pagination.removeClass('active');pagination.eq(4).addClass('active')}else if(slide.eq(1).attr('class')[14]=='f'){slide.removeClass('fade_in');slide.eq(0).addClass('fade_in');pagination.removeClass('active');pagination.eq(0).addClass('active')}else if(slide.eq(2).attr('class')[14]=='f'){slide.removeClass('fade_in');slide.eq(1).addClass('fade_in');pagination.removeClass('active');pagination.eq(1).addClass('active')}else if(slide.eq(3).attr('class')[14]=='f'){slide.removeClass('fade_in');slide.eq(2).addClass('fade_in');pagination.removeClass('active');pagination.eq(2).addClass('active')}else if(slide.eq(4).attr('class')[14]=='f'){slide.removeClass('fade_in');slide.eq(3).addClass('fade_in');pagination.removeClass('active');pagination.eq(3).addClass('active')}}
if(event.offsetX-drag_x1<-100){if(slide.eq(0).attr('class')[14]=='f'){slide.removeClass('fade_in');slide.eq(1).addClass('fade_in');pagination.removeClass('active');pagination.eq(1).addClass('active')}else if(slide.eq(1).attr('class')[14]=='f'){slide.removeClass('fade_in');slide.eq(2).addClass('fade_in');pagination.removeClass('active');pagination.eq(2).addClass('active')}else if(slide.eq(2).attr('class')[14]=='f'){slide.removeClass('fade_in');slide.eq(3).addClass('fade_in');pagination.removeClass('active');pagination.eq(3).addClass('active')}else if(slide.eq(3).attr('class')[14]=='f'){slide.removeClass('fade_in');slide.eq(4).addClass('fade_in');pagination.removeClass('active');pagination.eq(4).addClass('active')}else if(slide.eq(4).attr('class')[14]=='f'){slide.removeClass('fade_in');slide.eq(0).addClass('fade_in');pagination.removeClass('active');pagination.eq(0).addClass('active')}}}
function dragSlide(page,slide12345){let pagination=page;let slide=slide12345;if(event.offsetX-drag_x1>100){if(slide.eq(0).attr('class').substring(slide.eq(0).attr('class').length-7)=='fade_in'){slide.removeClass('fade_in');slide.eq(2).addClass('fade_in');pagination.removeClass('active');pagination.eq(2).addClass('active')}else if(slide.eq(1).attr('class').substring(slide.eq(1).attr('class').length-7)=='fade_in'){slide.removeClass('fade_in');slide.eq(0).addClass('fade_in');pagination.removeClass('active');pagination.eq(0).addClass('active')}else if(slide.eq(2).attr('class').substring(slide.eq(2).attr('class').length-7)=='fade_in'){slide.removeClass('fade_in');slide.eq(1).addClass('fade_in');pagination.removeClass('active');pagination.eq(1).addClass('active')}}
if(event.offsetX-drag_x1<-100){if(slide.eq(0).attr('class').substring(slide.eq(0).attr('class').length-7)=='fade_in'){slide.removeClass('fade_in');slide.eq(1).addClass('fade_in');pagination.removeClass('active');pagination.eq(1).addClass('active')}else if(slide.eq(1).attr('class').substring(slide.eq(1).attr('class').length-7)=='fade_in'){slide.removeClass('fade_in');slide.eq(2).addClass('fade_in');pagination.removeClass('active');pagination.eq(2).addClass('active')}else if(slide.eq(2).attr('class').substring(slide.eq(2).attr('class').length-7)=='fade_in'){slide.removeClass('fade_in');slide.eq(0).addClass('fade_in');pagination.removeClass('active');pagination.eq(0).addClass('active')}}}